import React, { Component } from 'react';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';
import * as apiCalls from './api'; 

class TodoList extends Component{
    constructor(props){
        super(props);
        this.state = {
            todos: []
        }
        this.addTodo = this.addTodo.bind(this);
    }

    componentDidMount(){
        this.loadTodos();
    }

    async loadTodos(){
        let allTodos = await apiCalls.getTodos();
        this.setState({todos})
    }

    addTodo(val){
        // console.log("Adding Todo from TodoList Component:", val)
        fetch(APIURL, {
            method: 'post',
            headers: new Headers({'Content-Type': 'application/json',
        }),
        body: JSON.stringify({name: val})
    })
        .then(resp => {
            if(!resp.ok){
                if(resp.status >= 400 && resp.status <= 500 ){
                    return resp.json().then(data => {
                        let err = {errorMessage : data.message};
                        throw err;
                    })
                }else{
                    let err = {errorMessage : 'Please Try Again Later'};
                    throw err;
                }
            }
            return resp.json();
        })
        .then(newTodo => {
            this.setState({todos: [...this.state.todos, newTodo]});
        });
    }

    deleteTodo(id){
        const deleteUrl = APIURL + id;
        fetch(deleteUrl, {
            method: 'delete',
        })
        .then(resp => {
            if(!resp.ok){
                if(resp.status >= 400 && resp.status <= 500 ){
                    return resp.json().then(data => {
                        let err = {errorMessage : data.message};
                        throw err;
                    })
                }else{
                    let err = {errorMessage : 'Please Try Again Later'};
                    throw err;
                }
            }
            return resp.json();
        })
        .then(() => {
            const todos = this.state.todos.filter(todo => todo._id !== id)
            this.setState({todos: todos})
        })
    }

    toggleTodo(todo){
        const updateUrl = APIURL + todo._id;
        fetch(updateUrl, {
            method: 'put',
            headers: new Headers({'Content-Type': 'application/json',
        }),
        body: JSON.stringify({completed: !todo.completed})
        })
        .then(resp => {
            if(!resp.ok){
                if(resp.status >= 400 && resp.status <= 500 ){
                    return resp.json().then(data => {
                        let err = {errorMessage : data.message};
                        throw err;
                    })
                }else{
                    let err = {errorMessage : 'Please Try Again Later'};
                    throw err;
                }
            }
            return resp.json();
        })
        .then((updatedTodo) => {
            const todos = this.state.todos.map(todo => 
                (todo._id === updatedTodo._id)? {...todo,completed: !todo.completed}:todo
            )
            this.setState({todos: todos})
        })
    }

    render(){
        const todos = this.state.todos.map((todo) => (
            <TodoItem 
                key={todo._id} 
                {...todo} 
                onDelete={this.deleteTodo.bind(this,todo._id)}
                onToggle={this.toggleTodo.bind(this,todo)}
            />
        ));

        return(
        <div>
            <h1>TodosList React App</h1>
            <TodoForm addTodo={this.addTodo}/>
            <ul>
                {todos}
            </ul>
        </div>

        );
    }
}

export default TodoList;
