const APIURL = '/api/todos/';


export async function getTodos(){
    return fetch(APIURL)
        .then(resp => {
            if(!resp.ok){
                if(resp.status >= 400 && resp.status <= 500 ){
                    return resp.json().then(data => {
                        let err = {errorMessage : data.message};
                        throw err;
                    })
                }else{
                    let err = {errorMessage : 'Please Try Again Later'};
                    throw err;
                }
            }
            return resp.json();
        })
}
