import React, { Component } from 'react';
import './App.css';

const APIURL = '/api/todos';

class TodoList extends Component{
    constructor(props){
        super(props);
        this.state = {
            todos: []
        }
    }

    componentDidMount(){
        this.loadTodos();
    }

    loadTodos(){
        fetch(APIURL)
        .then(data => data.json())
        .then(todos => this.setState({todos}));
    }
    render(){
        return(<h1>Todos React App</h1>);
    }
}

export default TodoList;
